#include <spi_boost/predef/detail/test.h>

#ifndef SPI_BOOST_PREDEF_MAKE_H
#define SPI_BOOST_PREDEF_MAKE_H

/*
Shorthands for the common version number formats used by vendors...
*/

/*`
[heading `SPI_BOOST_PREDEF_MAKE_..` macros]

These set of macros decompose common vendor version number
macros which are composed version, revision, and patch digits.
The naming convention indicates:

* The base of the specified version number. "`SPI_BOOST_PREDEF_MAKE_0X`" for
  hexadecimal digits, and "`SPI_BOOST_PREDEF_MAKE_10`" for decimal digits.
* The format of the vendor version number. Where "`V`" indicates the version digits,
  "`R`" indicates the revision digits, "`P`" indicates the patch digits, and "`0`"
  indicates an ignored digit.

Macros are:
*/
/*` `SPI_BOOST_PREDEF_MAKE_0X_VRP(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VRP(V) SPI_BOOST_VERSION_NUMBER((V&0xF00)>>8,(V&0xF0)>>4,(V&0xF))
/*` `SPI_BOOST_PREDEF_MAKE_0X_VVRP(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VVRP(V) SPI_BOOST_VERSION_NUMBER((V&0xFF00)>>8,(V&0xF0)>>4,(V&0xF))
/*` `SPI_BOOST_PREDEF_MAKE_0X_VRPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VRPP(V) SPI_BOOST_VERSION_NUMBER((V&0xF000)>>12,(V&0xF00)>>8,(V&0xFF))
/*` `SPI_BOOST_PREDEF_MAKE_0X_VVRR(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VVRR(V) SPI_BOOST_VERSION_NUMBER((V&0xFF00)>>8,(V&0xFF),0)
/*` `SPI_BOOST_PREDEF_MAKE_0X_VRRPPPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VRRPPPP(V) SPI_BOOST_VERSION_NUMBER((V&0xF000000)>>24,(V&0xFF0000)>>16,(V&0xFFFF))
/*` `SPI_BOOST_PREDEF_MAKE_0X_VVRRP(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VVRRP(V) SPI_BOOST_VERSION_NUMBER((V&0xFF000)>>12,(V&0xFF0)>>4,(V&0xF))
/*` `SPI_BOOST_PREDEF_MAKE_0X_VRRPP000(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VRRPP000(V) SPI_BOOST_VERSION_NUMBER((V&0xF0000000)>>28,(V&0xFF00000)>>20,(V&0xFF000)>>12)
/*` `SPI_BOOST_PREDEF_MAKE_0X_VVRRPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_0X_VVRRPP(V) SPI_BOOST_VERSION_NUMBER((V&0xFF0000)>>16,(V&0xFF00)>>8,(V&0xFF))
/*` `SPI_BOOST_PREDEF_MAKE_10_VPPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VPPP(V) SPI_BOOST_VERSION_NUMBER(((V)/1000)%10,0,(V)%1000)
/*` `SPI_BOOST_PREDEF_MAKE_10_VR0(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VR0(V) SPI_BOOST_VERSION_NUMBER(((V)/100)%10,((V)/10)%10,0)
/*` `SPI_BOOST_PREDEF_MAKE_10_VRP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VRP(V) SPI_BOOST_VERSION_NUMBER(((V)/100)%10,((V)/10)%10,(V)%10)
/*` `SPI_BOOST_PREDEF_MAKE_10_VRP000(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VRP000(V) SPI_BOOST_VERSION_NUMBER(((V)/100000)%10,((V)/10000)%10,((V)/1000)%10)
/*` `SPI_BOOST_PREDEF_MAKE_10_VRPPPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VRPPPP(V) SPI_BOOST_VERSION_NUMBER(((V)/100000)%10,((V)/10000)%10,(V)%10000)
/*` `SPI_BOOST_PREDEF_MAKE_10_VRPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VRPP(V) SPI_BOOST_VERSION_NUMBER(((V)/1000)%10,((V)/100)%10,(V)%100)
/*` `SPI_BOOST_PREDEF_MAKE_10_VRR(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VRR(V) SPI_BOOST_VERSION_NUMBER(((V)/100)%10,(V)%100,0)
/*` `SPI_BOOST_PREDEF_MAKE_10_VRRPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VRRPP(V) SPI_BOOST_VERSION_NUMBER(((V)/10000)%10,((V)/100)%100,(V)%100)
/*` `SPI_BOOST_PREDEF_MAKE_10_VRR000(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VRR000(V) SPI_BOOST_VERSION_NUMBER(((V)/100000)%10,((V)/1000)%100,0)
/*` `SPI_BOOST_PREDEF_MAKE_10_VV00(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VV00(V) SPI_BOOST_VERSION_NUMBER(((V)/100)%100,0,0)
/*` `SPI_BOOST_PREDEF_MAKE_10_VVRR(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VVRR(V) SPI_BOOST_VERSION_NUMBER(((V)/100)%100,(V)%100,0)
/*` `SPI_BOOST_PREDEF_MAKE_10_VVRRP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VVRRP(V) SPI_BOOST_VERSION_NUMBER(((V)/1000)%100,((V)/10)%100,(V)%10)
/*` `SPI_BOOST_PREDEF_MAKE_10_VVRRPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VVRRPP(V) SPI_BOOST_VERSION_NUMBER(((V)/10000)%100,((V)/100)%100,(V)%100)
/*` `SPI_BOOST_PREDEF_MAKE_10_VVRRPPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VVRRPPP(V) SPI_BOOST_VERSION_NUMBER(((V)/100000)%100,((V)/1000)%100,(V)%1000)
/*` `SPI_BOOST_PREDEF_MAKE_10_VVRR0PP00(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VVRR0PP00(V) SPI_BOOST_VERSION_NUMBER(((V)/10000000)%100,((V)/100000)%100,((V)/100)%100)
/*` `SPI_BOOST_PREDEF_MAKE_10_VVRR0PPPP(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VVRR0PPPP(V) SPI_BOOST_VERSION_NUMBER(((V)/10000000)%100,((V)/100000)%100,(V)%10000)
/*` `SPI_BOOST_PREDEF_MAKE_10_VVRR00PP00(V)` */
#define SPI_BOOST_PREDEF_MAKE_10_VVRR00PP00(V) SPI_BOOST_VERSION_NUMBER(((V)/100000000)%100,((V)/1000000)%100,((V)/100)%100)
/*`
[heading `SPI_BOOST_PREDEF_MAKE_*..` date macros]

Date decomposition macros return a date in the relative to the 1970
Epoch date. If the month is not available, January 1st is used as the month and day.
If the day is not available, but the month is, the 1st of the month is used as the day.
*/
/*` `SPI_BOOST_PREDEF_MAKE_DATE(Y,M,D)` */
#define SPI_BOOST_PREDEF_MAKE_DATE(Y,M,D) SPI_BOOST_VERSION_NUMBER((Y)%10000-1970,(M)%100,(D)%100)
/*` `SPI_BOOST_PREDEF_MAKE_YYYYMMDD(V)` */
#define SPI_BOOST_PREDEF_MAKE_YYYYMMDD(V) SPI_BOOST_PREDEF_MAKE_DATE(((V)/10000)%10000,((V)/100)%100,(V)%100)
/*` `SPI_BOOST_PREDEF_MAKE_YYYY(V)` */
#define SPI_BOOST_PREDEF_MAKE_YYYY(V) SPI_BOOST_PREDEF_MAKE_DATE(V,1,1)
/*` `SPI_BOOST_PREDEF_MAKE_YYYYMM(V)` */
#define SPI_BOOST_PREDEF_MAKE_YYYYMM(V) SPI_BOOST_PREDEF_MAKE_DATE((V)/100,(V)%100,1)

#endif
